import { Graph, KeyValue, LabelValue } from "./graph";
export interface INodeObj {
    v: KeyValue;
    value: LabelValue;
    parent: KeyValue | undefined;
}
export interface IEdgeObj {
    v: string;
    w: string;
    name: string | undefined;
    value: LabelValue;
}
export interface IJsonObj {
    options: {
        directed: boolean;
        multigraph: boolean;
        compound: boolean;
    };
    nodes: INodeObj[];
    edges: IEdgeObj[];
    value: LabelValue;
}
declare const json: {
    read: (jsonObj: IJsonObj) => Graph;
    write: (g: Graph) => IJsonObj;
};
export { json };
